import React, { useState } from "react";
import "tailwindcss/tailwind.css";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Bienvenue sur mon portfolio interactif ! Tapez 'help' pour voir les commandes disponibles."
  );

  function handleCommand(e) {
    e.preventDefault();
    switch (command) {
      case "help":
        setOutput(
          "Les commandes disponibles sont : help, projets, compétences."
        );
        break;
      case "projets":
        setOutput("Voici la liste de mes projets : Projet A, Projet B, Projet C.");
        break;
      case "compétences":
        setOutput(
          "Je suis compétent en HTML, CSS, JavaScript, React JS, Vue JS, et Python."
        );
        break;
      default:
        setOutput(`La commande '${command}' n'est pas reconnue. Entrez 'help' pour voir les commandes disponibles.`);
    }
    setCommand("");
  }

  function handleChange(e) {
    setCommand(e.target.value);
  }

  return (
    <div className="h-screen bg-black flex flex-col justify-center items-center">
      <div className="text-white text-center flex flex-col max-w-lg w-full">
        <h1 className="text-green-500 font-bold text-4xl mb-4">
          Mon Portfolio Interactif
        </h1>
        <div className="mb-2">{welcomeMessage}</div>
        <form onSubmit={handleCommand} className="flex mb-2">
          <div className="mr-2 text-green-500 self-end">$</div>
          <input
            type="text"
            value={command}
            onChange={handleChange}
            className="flex-grow bg-transparent text-white focus:outline-none"
            autoFocus
          />
        </form>
        {output && (
          <div className="p-4 bg-white rounded-md shadow-md">
            <pre className="text-black">{output}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default Terminal;
