import React, { useState } from "react";
import "tailwindcss/tailwind.css";
import { HiTerminal } from "react-icons/hi";
import { RiCloseLine } from "react-icons/ri";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Bienvenue sur mon portfolio interactif ! Tapez 'help' pour voir les commandes disponibles."
  );

  function handleCommand(e) {
    e.preventDefault();
    switch (command) {
      case "help":
        setOutput(
          "Les commandes disponibles sont : help, projets, compétences."
        );
        break;
      case "projets":
        setOutput("Voici la liste de mes projets : Projet A, Projet B, Projet C.");
        break;
      case "compétences":
        setOutput(
          "Je suis compétent en HTML, CSS, JavaScript, React JS, Vue JS, et Python."
        );
        break;
      default:
        setOutput(`La commande '${command}' n'est pas reconnue. Entrez 'help' pour voir les commandes disponibles.`);
    }
    setCommand("");
  }

  function handleChange(e) {
    setCommand(e.target.value);
  }

  return (
    <div className="h-screen bg-gray-900 flex justify-center items-center">
      <div className="relative rounded-lg shadow-lg overflow-hidden">
        <div className="absolute inset-0 bg-gray-800"></div>
        <div className="absolute inset-0 border-2 border-gray-600"></div>
        <div className="absolute inset-0 border-2 border-gray-900"></div>
        <div className="absolute inset-x-0 top-0 flex justify-center items-center h-8 text-gray-400">
          <HiTerminal size={18} />
        </div>
        <div className="absolute top-0 right-0 flex justify-center items-center h-8 text-gray-400">
          <RiCloseLine size={18} />
        </div>
        <div className="p-4">
          <div className="text-white text-center flex flex-col">
            <h1 className="text-green-500 font-bold text-4xl mb-4">
              Mon Portfolio Interactif
            </h1>
            <div className="mb-2">{welcomeMessage}</div>
            <form onSubmit={handleCommand} className="flex">
              <div className="mr-2 text-green-500 self-end">$</div>
              <input
                type="text"
                value={command}
                onChange={handleChange}
                className="flex-grow bg-transparent text-white focus:outline-none"
              />
            </form>
            {output && (
              <div className="mt-2">
                <pre>{output}</pre>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Terminal;
