import React, { useState, useEffect, useRef } from "react";
import "tailwindcss/tailwind.css";
import github from "./icons/github.png";
import behance from "./icons/behance.png";
import gmail from "./icons/gmail.png";
import linkedin from "./icons/linkedin.png";
import medium from "./icons/medium.png";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState([]);
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Welcome to my portfolio! To start exploring this Terminal type <i>help</i> then press enter. Note that the Tab Key helps you complete the commands."
  );

  const terminalRef = useRef(null);

  useEffect(() => {
    terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
  }, [output]);

  function handleCommand(e) {
    e.preventDefault();
    let newOutput = [...output, { command: command, output: "" }];
    switch (command.toLowerCase()) {
      case "about":
        newOutput[newOutput.length - 1].output = `My name is Fatima STAILI, I am a cybersecurity student who has had a long-standing passion for technology and security and a slight web development background, I am currently working on networking certifications. I am a bachelor student in cyber security and a member of the development department of the GDG club, based in Orleans.`;
        break;
      case "work":
        newOutput[newOutput.length - 1].output = (
          <>
            <table className="table-auto">
              <thead>
                <tr>
                  <th className="px-4 py-2">Date</th>
                  <th className="px-4 py-2">Entreprise</th>
                  <th className="px-4 py-2">Role</th>
                  <th className="px-4 py-2">Description</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2">Digital Mediator</td>
                  <td className="border px-4 py-2"></td>
                </tr>
                <tr>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2">Assistant Chief Information Security Officer (CISO)</td>
                  <td className="border px-4 py-2"></td>
                </tr>
                <tr>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2">Assistant System and Network Administrator</td>
                  <td className="border px-4 py-2"></td>
                </tr>
                <tr>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2"></td>
                  <td className="border px-4 py-2">Network Technician</td>
                  <td className="border px-4 py-2"></td>
                </tr>
              </tbody>
            </table>
          </>
        );
        break;
      case "contact":
        newOutput[newOutput.length - 1].output = (
          <>
            <div className="flex justify-around my-4">
              <a href="mailto:example@gmail.com" target="_blank" rel="noopener noreferrer">
                <img src={gmail} alt="gmail" />
              </a>
              <a href="https://www.linkedin.com/in/staili/" target="_blank" rel="noopener noreferrer">
                <img src={linkedin} alt="linkedin" />
              </a>
              <a href="https://github.com/staili" target="_blank" rel="noopener noreferrer">
                <img src={github} alt="github" />
              </a>
              <a href="https://www.behance.net/staili" target="_blank" rel="noopener noreferrer">
                <img src={behance} alt="behance" />
              </a>
              <a href="https://medium.com/@staili" target="_blank" rel="noopener noreferrer">
                <img src={medium} alt="medium" />
              </a>
            </div>
          </>
        );
        break;
      case "skills":
        newOutput[newOutput.length - 1].output = `Programming languages: Python, JavaScript, TypeScript, Go, Java, C
Frameworks: React, Vue, Angular, Django, Flask
Tools: Docker, Kubernetes, Jenkins, Travis CI, Git, Jira, Trello
Networks: TCP/IP, Firewall, IDS, IPS, VPN, Wireshark, OpenVAS
Operating Systems: Linux, Windows, MacOS
Languages: French (native), English (C1), Arabic (native)
Others: ElasticSearch, Logstash, Kibana, Prometheus, Grafana`;
        break;
      case "education":
        newOutput[newOutput.length - 1].output = `Bachelor of Science in Cybersecurity, INSA Centre Val de Loire
Diploma of Higher Education in Network and Telecommunications, IUT Orleans
Bachelor of Science in Computer Science, University of Orleans`;
        break;
      case "help":
        newOutput[newOutput.length - 1].output = `about: about me
contact: how to contact me
work: my work experience
skills: my skills
education: my education`;
        break;
      default:
        newOutput[newOutput.length - 1].output = `Unknown command: ${command}`;
        break;
    }
    setOutput(newOutput);
    setCommand("");
  }

  return (
    <div className="flex flex-col w-full h-full overflow-hidden">
      <div className="flex-grow bg-gray-800 p-4 overflow-auto break-words" ref={terminalRef}>
        <div dangerouslySetInnerHTML={{ __html: welcomeMessage }} className="text-white mb-4"></div>
        {output.map(({ command, output }, index) => (
          <div className="flex text-white mb-4" key={index}>
            <div className="mr-2 text-green-500 font-mono">visitor@terminal.staili : ~$</div>
            <div className="flex-grow">
              <div className="text-green-500 font-mono">{command}</div>
              <div className="ml-8">{output}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="h-14 bg-gray-900 flex items-center px-4">
        <form onSubmit={handleCommand} className="w-full">
          <div className="flex items-center text-white">
            <div className="mr-2 text-green-500 font-mono">visitor@terminal.staili : ~$</div>
            <input
              type="text"
              className="w-full bg-gray-900 text-white focus:outline-none"
              onChange={(e) => setCommand(e.target.value)}
              value={command}
            />
          </div>
        </form>
      </div>
    </div>
  );
}

export default Terminal;
