import React, { useState, useEffect, useRef } from "react";
import "tailwindcss/tailwind.css";
import github from "./icons/github.png";
import behance from "./icons/behance.png";
import gmail from "./icons/gmail.png";
import linkedin from "./icons/linkedin.png";
import medium from "./icons/medium.png";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState([]);
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Welcome to my portfolio! To start exploring this Terminal type <i>help</i> then press enter. Note that the Tab Key helps you complete the commands."
  );

  const terminalRef = useRef(null);

  useEffect(() => {
    terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
  }, [output]);

  function handleCommand(e) {
    e.preventDefault();
    let newOutput = [...output, { command: command, output: "" }];
    switch (command.toLowerCase()) {
      case "about":
        newOutput[newOutput.length - 1].output = `My name is Fatima STAILI, I am a cybersecurity student who has had a long-standing passion for technology and security and a slight web developement background, I am currently working on networking certifications. I am a bashlor student in cyber security and a member 
        of the development department of the GDG club, based in Orleans.`;
        break;
      case "work":
        newOutput[newOutput.length - 1].output = (
          <>
            <table className="table-fixed">
              <thead>
                <tr>
                  <th className="w-1/4">Date</th>
                  <th className="w-1/4">Company</th>
                  <th className="w-1/4">Role</th>
                  <th className="w-1/4">Description</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td></td>
                  <td></td>
                  <td>Digital Mediator</td>
                  <td></td>
                </tr>
                <tr>
                  <td></td>
                  <td></td>
                  <td>Assistant Chief Information Security Officer (CISO)</td>
                  <td></td>
                </tr>
                <tr>
                  <td></td>
                  <td></td>
                  <td>Assistant System and Network Administrator</td>
                  <td></td>
                </tr>
                <tr>
                  <td></td>
                  <td></td>
                  <td>Network Technician</td>
                  <td></td>
                </tr>
              </tbody>
            </table>
          </>
        );
        break;
      //... the rest of your cases
    }
    setOutput(newOutput);
    setCommand("");
  }

  // the rest of your code...

  return (
    <div className="bg-black h-screen w-screen flex justify-center items-center">
      {/* ... */}
      <div className="mt-2 text-blue-500 font-mono">visitor@terminal.staili : ~$ {command}</div>
      <div className="mt-2">
        <pre className="text-green-500">{output}</pre>
      </div>
      {/* ... */}
    </div>
  );
}

export default Terminal;
