import React, { useState } from "react";
import "tailwindcss/tailwind.css";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Bienvenue sur mon portfolio interactif ! Tapez 'help' pour voir les commandes disponibles."
  );

  function handleCommand(e) {
    e.preventDefault();
    switch (command) {
      case "help":
        setOutput(
          "Les commandes disponibles sont : help, projets, compétences."
        );
        break;
      case "projets":
        setOutput("Voici la liste de mes projets : Projet A, Projet B, Projet C.");
        break;
      case "compétences":
        setOutput(
          "Je suis compétent en HTML, CSS, JavaScript, React JS, Vue JS, et Python."
        );
        break;
      default:
        setOutput(`La commande '${command}' n'est pas reconnue. Entrez 'help' pour voir les commandes disponibles.`);
    }
    setCommand("");
  }

  function handleChange(e) {
    setCommand(e.target.value);
  }

  return (
    <div className="h-screen bg-gray-800">
      <div className="flex flex-col h-full">
        <div className="bg-gray-900 h-8 flex items-center">
          <span className="text-green-500 ml-3">
            &lt;root@localhost&gt;
          </span>
          <span className="text-white ml-2">
            {welcomeMessage}
          </span>
        </div>
        <div className="flex flex-grow items-end">
          <form onSubmit={handleCommand} className="w-full px-3">
            <span className="text-green-500">&gt;</span>
            <input
              type="text"
              value={command}
              onChange={handleChange}
              className="bg-gray-900 text-white w-full focus:outline-none ml-2"
              autoFocus
            />
          </form>
        </div>
        {output && (
          <div className="bg-gray-900 text-white p-3">
            <pre>{output}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default Terminal;
