import React, { useState } from "react";
import "tailwindcss/tailwind.css";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Bienvenue sur mon portfolio interactif ! Tapez 'help' pour voir les commandes disponibles."
  );

  function handleCommand(e) {
    e.preventDefault();
    switch (command) {
      // ...
    }
    setCommand("");
  }

  function handleChange(e) {
    setCommand(e.target.value);
  }

  return (
    <div className="bg-black h-screen w-screen flex justify-center items-center">
      <div className="bg-gray-900 text-white text-center flex flex-col rounded-lg w-2/3 h-3/4">
        <div className="bg-gray-700 p-2 rounded-t-lg flex items-center justify-between">
          <div className="flex space-x-2">
            <div className="text-green-500">&#x25CF;</div>
            <div className="text-yellow-500">&#x25CF;</div>
            <div className="text-red-500">&#x25CF;</div>
          </div>
          <div className="text-gray-300">~/Portfolio/terminal</div>
          <div className="text-red-500 cursor-pointer">&#x2716;</div>
        </div>
        <div className="bg-gray-800 p-2 flex">
          <div className="text-gray-300 flex">
            <span className="mr-2">File</span>
            <span className="mr-2">|</span>
            <span className="mr-2">Actions</span>
            <span className="mr-2">|</span>
            <span className="mr-2">Edit</span>
            <span className="mr-2">|</span>
            <span className="mr-2">View</span>
            <span className="mr-2">|</span>
            <span>Help</span>
          </div>
        </div>
        <div className="flex-grow bg-gray-800 p-4">
          <div className="mb-2">{welcomeMessage}</div>
          <form onSubmit={handleCommand} className="flex">
            <div className="mr-2 text-blue-600 font-mono self-end">visitor@terminal.staili : ~$</div>
            <input
              type="text"
              value={command}
              onChange={handleChange}
              className="flex-grow bg-transparent text-white focus:outline-none border-b-2 border-green-500 font-mono"
            />
          </form>
          {output && (
            <div className="mt-2">
              <pre>{output}</pre>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Terminal;
