import React, { useState } from "react";
import "tailwindcss/tailwind.css";

function Terminal() {
  const [command, setCommand] = useState("");
  const [output, setOutput] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState(
    "Welcome to my portfolio! To start exploring this Terminal type <i>help</i> then press enter. Note that the Tab Key helps you complete the commands."
  );

  function handleCommand(e) {
    e.preventDefault();
    switch (command.toLowerCase()) {
      case "about":
        setOutput("njkdjklnvkljnoijinoefd");
        break;
      case "contact":
        setOutput(
          <>
            <img src="/path_to_icons_folder/gmail.svg" alt="Mail Icon" className="inline" /> staili.fatimazohra@gmail.com
          </>
        );
        break;
      case "socials":
        setOutput(
          <>
            <img src="/path_to_icons_folder/linkedin.svg" alt="LinkedIn Icon" className="inline" />{" "}
            <a href="https://www.linkedin.com/in/fatima-zohra-staili-681878200/" target="_blank" rel="noopener noreferrer">
              Linkedin
            </a>
            <br />
            <img src="/path_to_icons_folder/medium.svg" alt="Medium Icon" className="inline" />{" "}
            <a href="https://medium.com/@staili.fatimazohra" target="_blank" rel="noopener noreferrer">
              Medium
            </a>
            <br />
            <img src="/path_to_icons_folder/behance.svg" alt="Behance Icon" className="inline" />{" "}
            <a href="https://www.behance.net/fatimastaili" target="_blank" rel="noopener noreferrer">
              Behance
            </a>
          </>
        );
        break;
      case "help":
        setOutput(`
          ${"about".padEnd(15, ' ')}=> learn more about me
          ${"contact".padEnd(15, ' ')}=> get my e-mail address
          ${"socials".padEnd(15, ' ')}=> how to contact me online
          ${"education".padEnd(15, ' ')}=> my academic journey
          ${"experience".padEnd(15, ' ')}=> display the list of my work experiences
          ${"projects".padEnd(15, ' ')}=> display the list of my projects
          ${"hobbies".padEnd(15, ' ')}=> know more about what I like
          ${"clear".padEnd(15, ' ')}=> clear terminal
          ${"get cv".padEnd(15, ' ')}=> download my cv
        `);
        break;
      default:
        setOutput("Unknown command. Type 'help' for a list of available commands.");
    }
    setCommand("");
  }

  function handleChange(e) {
    setCommand(e.target.value);
  }

  return (
    <div className="bg-black h-screen w-screen flex justify-center items-center">
      <div className="bg-gray-900 text-white text-center flex flex-col rounded-lg w-2/3 h-3/4">
        {/* Le reste de votre code */}
      </div>
    </div>
  );
}

export default Terminal;
